				}
				if (num == 2463 && (!Main.hardMode || !WorldGen.crimson))
				{
					flag = true;
				}
				if (num == 2465 && !Main.hardMode)
				{
					flag = true;
				}
				if (num == 2468 && !Main.hardMode)
				{
					flag = true;
				}
				if (num == 2471 && !Main.hardMode)
				{
					flag = true;
				}
				if (num == 2473 && !Main.hardMode)
				{
					flag = true;
				}
				if (num == 2477 && !WorldGen.crimson)
				{
					flag = true;
				}
				if (num == 2480 && !Main.hardMode)
				{
					flag = true;
