		protected Microsoft.Xna.Framework.Color randColor()
		{
			int i;
			int num = 0;
			int num1 = 0;
			for (i = 0; num + i + num1 <= 150; i = Main.rand.Next(256))
			{
				num = Main.rand.Next(256);
				num1 = Main.rand.Next(256);
			}
			return new Microsoft.Xna.Framework.Color(num, num1, i, 255);
		}

