							{
								this.velocity.X = 0f;
								return;
							}
						}
						else
						{
							this.velocity.X = this.velocity.X + (float)num * 0.01f;
							if (this.velocity.X > 0.2f)
							{
								this.velocity.X = this.velocity.X * 0.95f;
							}
							if (this.velocity.X < -0.2f)
							{
								this.velocity.X = this.velocity.X * 0.95f;
								return;
							}
						}
					}
				}
