			public static void Wall(int x, int y, int wallType)
			{
				if (!WorldGen.InWorld(x, y, 0))
				{
					return;
				}
				byte wall = (byte)wallType;
				List<Point> list = new List<Point>();
				List<Point> list2 = new List<Point>();
				HashSet<Point> hashSet = new HashSet<Point>();
				list2.Add(new Point(x, y));
				while (list2.Count > 0)
				{
					list.Clear();
					list.AddRange(list2);
					list2.Clear();
					while (list.Count > 0)
					{
						Point item = list[0];
						if (!WorldGen.InWorld(item.X, item.Y, 1))
						{
							list.Remove(item);
						}
						else
						{
							hashSet.Add(item);
							list.Remove(item);
							Tile tile = Main.tile[item.X, item.Y];
							if (WorldGen.SolidTile(item.X, item.Y) || tile.wall != WallID.None)
							{
								if (tile.active() && tile.wall == WallID.None)
								{
									tile.wall = wall;
								}
							}
							else
							{
								tile.wall = wall;
								Point item2 = new Point(item.X - 1, item.Y);
								if (!hashSet.Contains(item2))
								{
									list2.Add(item2);
								}
								item2 = new Point(item.X + 1, item.Y);
								if (!hashSet.Contains(item2))
								{
									list2.Add(item2);
								}
								item2 = new Point(item.X, item.Y - 1);
								if (!hashSet.Contains(item2))
								{
									list2.Add(item2);
								}
								item2 = new Point(item.X, item.Y + 1);
								if (!hashSet.Contains(item2))
								{
									list2.Add(item2);
								}
							}
						}
					}
				}
			}

