						WorldFile.loadWorld();
						if (WorldGen.loadFailed || !WorldGen.loadSuccess)
						{
							WorldFile.loadWorld();
							if (WorldGen.loadFailed || !WorldGen.loadSuccess)
							{
								FileUtilities.Copy(Main.worldPathName, Main.worldPathName + ".bak", true);
								FileUtilities.Copy(Main.worldPathName + ".bad", Main.worldPathName, true);
