		public static void tileCounterNext(int x, int y)
		{
			if (WorldGen.tileCounterNum >= WorldGen.tileCounterMax)
			{
				return;
			}
			if (x < 5 || x > Main.maxTilesX - 5 || y < 5 || y > Main.maxTilesY - 5)
